import 'package:dough/dough.dart';import 'package:flutter/material.dart';void main() => runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      debugShowCheckedModeBanner: false,      home: DemoApp(),    );  }}class DemoApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    final myDraggableChild = Container(      height: 140,      width: 140,      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(20),        color: Colors.redAccent,      ),      child: Center(        child: Text('Data',style: TextStyle(fontSize: 20),),      ),    );    final myFeedbackChild = Container(      height: 140,      width: 140,      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(20),        color: Colors.green,      ),      child: Center(        child: Text('Dragging',style: TextStyle(fontSize: 20),),      ),    );    final myDraggable = DoughRecipe(        data: DoughRecipeData(          viscosity: 500,          adhesion: 4,          draggablePrefs: DraggableDoughPrefs(            breakDistance: 80,            useHapticsOnBreak: true,          )        ),        child: DraggableDough<String>(          child: myDraggableChild,          feedback: myFeedbackChild,          data: 'My Data!',          onDragCompleted: (){            print('Data is Dragged');          },          onDoughBreak: (){            print('Data is being Dragged');          },        )    );    final myDraggedTarget = DragTarget<String>(        builder: (context, candidateData, rejectedData){          return Container(            height: 140,            width: 140,            decoration: BoxDecoration(              borderRadius: BorderRadius.circular(10),              color: candidateData.length > 0 ? Colors.greenAccent : Colors.grey,            ),            child: Center(              child: Text('Drag Here',style: TextStyle(fontSize: 20),),            ),          );        },      onWillAccept: (value){          return value == 'My Data!';      },      onAccept: (value){          print('Data is Accepted!');      },    );    return Scaffold(      appBar: AppBar(        title: Text('Demo App'),      ),      body: Stack(        children: [          Positioned(            top: 50,              left: 50,              child: myDraggable,          ),          Positioned(            bottom: 50,              right: 50,              child: myDraggedTarget          ),        ],      ),    );  }}